// import "./App.css";
import { useState, useEffect } from "react";

function getStory(id) {
  return fetch(
    `https://hacker-news.firebaseio.com/v0/item/${id}.json?print=pretty`
  ).then((response) => {
    if (response.ok) {
      return response.json();
    }
    throw new Error("Could not fetch story id");
  });
}

function App() {
  const [data, setData] = useState([]);
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  //https://hacker-news.firebaseio.com/v0/item/12.json?print=pretty
  //https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty

  useEffect(() => {
    fetch(`https://hacker-news.firebaseio.com/v0/newstories.json?print=pretty`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Could not fetch data");
      })
      .then((myJson) => {
        // console.log(myJson);
        setLoading(true);
        setData(myJson);
        // setData(myJson);
        const myPromises = [];
        for (let id of myJson) {
          // console.log("id of myJson", id);
          myPromises.push(getStory(id));
        }
        return Promise.all(myPromises).then((stories) => setStories(stories));
      });
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!loading) {
    return <div>Loading...</div>;
  } else {
    return (
      <div className="App">
        <div>
          <pre>{JSON.stringify(stories, null)}</pre>{" "}
          {/* {data.map((storyId) => (
            // <div key={storyId}>{JSON.stringify(story.title)}</div>
            // <div key={storyId}>{story.title}</div>
          ))} */}
        </div>
      </div>
    );
  }
}

export default App;
